package cn.onetozero.easy.annotations.models;import java.lang.annotation.*;/** * @author 徐卫超 * @since 2019/6/21 11:45 * 功能：逻辑删除字段 用于控制数据逻辑删除标注 * 备注： */@Documented@Target({ElementType.FIELD})@Retention(RetentionPolicy.RUNTIME)public @interface Logic {    /**     * @return 有效值     */    String valid() default "1";    /**     * @return 无效值     */    String invalid() default "0";    /**     * @return 属性和数据表之间的列关系     */    String value() default "";    /**     * @return 查询是否查询该字段 false 查询 true 不查询     */    boolean selectIgnore() default true;}