package com.xwc.open.easy.core.annotations;import com.xwc.open.easy.core.enums.FillType;import java.lang.annotation.*;/** * 创建人：徐卫超 * 时间：2019/8/3 17:23 * 功能： * 备注： */@Documented@Target({ElementType.FIELD})@Retention(RetentionPolicy.RUNTIME)public @interface FillColumn {    /**     * 属性和数据表之间的列关系     *     * @return     */    String value() default "";    /**     * 填充类型  INSERT 类型会在插入的时候生效  UPDATE类型插入和更新的时候生效     *     * @return     */    FillType type() default FillType.CHANGE;    /**     * 填充的标识属性 默认使用属性名字作为填充标识     *     * @return     */    String identification() default "";    /**     * 查询是否查询该字段 false 查询 true 不查询     *     * @return     */    boolean selectIgnore() default false;}