package com.xwc.open.easybatis.core.anno.table;import com.xwc.open.easybatis.core.enums.FillType;import java.lang.annotation.*;/** * 创建人：徐卫超 * 时间：2019/8/3 17:23 * 功能： * 备注： */@Documented@Target({ElementType.ANNOTATION_TYPE})@Retention(RetentionPolicy.RUNTIME)@Inheritedpublic @interface FieldFill {    /**     * 填充类型  INSERT 类型会在插入的时候生效  UPDATE类型插入和更新的时候生效     *     * @return     */    FillType type();    /**     * 审计填充的唯一标识  默认使用属性名来作为唯一标识     *     * @return     */    String attribute() default "";}