package com.xwc.open.easybatis.core.anno.table;import java.lang.annotation.*;/** * 创建人：徐卫超 * 时间：2019/6/21 11:45 * 功能： * 备注： */@Documented@Target({ElementType.FIELD, ElementType.METHOD})@Retention(RetentionPolicy.RUNTIME)public @interface Logic {    /**     * 有效值     *     * @return     */    int valid();    /**     * 无效值     *     * @return     */    int invalid();    /**     * 属性和数据表之间的列关系     *     * @return     */    String value() default "";    /**     * 查询是否查询该字段 false 查询 true 不查询     *     * @return     */    boolean selectIgnore() default true;}