package com.xwc.open.easybatis.core.anno.table.fill;import com.xwc.open.easybatis.core.anno.table.FieldFill;import com.xwc.open.easybatis.core.enums.FillType;import java.lang.annotation.Documented;import java.lang.annotation.ElementType;import java.lang.annotation.Inherited;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/** * 创建人：徐卫超 * 时间：2019/8/3 15:13 * 功能： * 备注： */@Documented@Target({ElementType.FIELD})@Retention(RetentionPolicy.RUNTIME)@Inherited@FieldFill(type = FillType.UPDATE)public @interface UpdateName {    /**     * 属性和数据表之间的列关系     *     * @return     */    String value() default "";    /**     * 查询是否查询该字段 false 查询 true 不查询     *     * @return     */    boolean selectIgnore() default true;    /**     * 审计填充的唯一标识  默认使用属性名来作为唯一标识     *     * @return     */    String attribute() default "";}