package com.xwc.open.easybatis.core.enums;import org.apache.ibatis.mapping.SqlCommandType;/** * 创建人：徐卫超 * 时间：2019/8/3 17:25 * 功能： * 备注： */public enum AuditorType {    /**     * 创建时间     */    CREATE_TIME(SqlCommandType.INSERT),    /**     * 更新时间     */    UPDATE_TIME(SqlCommandType.UPDATE),    /**     * 创建者名字     */    CREATE_NAME(SqlCommandType.INSERT),    /**     * 更新者名字     */    UPDATE_NAME(SqlCommandType.UPDATE),    /**     * 创建者ID     */    CREATE_ID(SqlCommandType.INSERT),    /**     * 更新者ID     */    UPDATE_ID(SqlCommandType.UPDATE);    private SqlCommandType sqlCommandType;    AuditorType(SqlCommandType sqlCommandType) {        this.sqlCommandType = sqlCommandType;    }    public SqlCommandType command() {        return sqlCommandType;    }}