package com.xwc.open.easybatis.core.enums;/** * 创建人：徐卫超 * 时间：2019/8/4 9:59 * 功能： * 备注： */public enum ConditionType {    EQUAL("等于", "="),    NOT_EQUAL("不等于", "!="),    GT("大于号", "<![CDATA[>]]>"),    LT("小于号", "<![CDATA[<]]>"),    GTQ("大于等于", "<![CDATA[>=]]>"),    LTQ("小于等于", "<![CDATA[<=]]>"),    IS_NULL("为空", "IS NULL"),    NOT_NULL("不为空", "IS NOT NULL"),    LIKE("全模糊匹配", "LIKE CONCAT('%%',%s,'%%')"),    LEFT_LIKE("左模糊匹配", "LIKE CONCAT('%%',%s)"),    RIGHT_LIKE("右模糊匹配", "LIKE CONCAT(%s,'%%')"),    NOT_LIKE("全模糊匹配", "NOT LIKE CONCAT('%%',%s,'%%')"),    NOT_LEFT_LIKE("左模糊匹配", "NOT LIKE CONCAT('%%',%s)"),    NOT_RIGHT_LIKE("右模糊匹配", "NOT LIKE CONCAT(%s,'%%')"),    IN("包含", "IN"),    NOT_IN("不包含", "NOT IN"),    BETWEEN("范围", "BETWEEN %s AND %s"),    NOT_BETWEEN("不在范围", "BETWEEN %s AND %s"),    ORDER_BY_ASC("排序", "ASC"),    ORDER_BY_DESC("排序", "DESC"),    ORDER_BY("排序", ""),    START("分页下标", ""),    OFFSET("分页偏移量", ""),    SET_PARAM("赋值", ""),    NONE("其他", ""),    IGNORE("忽略", "");    private String description;    private String expression;    ConditionType(String description, String expression) {        this.description = description;        this.expression = expression;    }    public String expression() {        return expression;    }}