package com.xwc.open.easybatis.core.excp;/** * 创建人：徐卫超 * 时间：2019/10/14 16:53 * 功能：Easybatis 所有异常的基类 * 备注： */public class EasyBatisException extends RuntimeException {    public EasyBatisException() {    }    public EasyBatisException(String message) {        super(message);    }    public EasyBatisException(String message, Throwable cause) {        super(message, cause);    }    public EasyBatisException(Throwable cause) {        super(cause);    }    public EasyBatisException(String message, Throwable cause, boolean enableSuppression, boolean writableStackTrace) {        super(message, cause, enableSuppression, writableStackTrace);    }}