package com.xwc.open.easybatis.core.model.table;import lombok.Getter;import lombok.Setter;import org.apache.ibatis.mapping.SqlCommandType;/** * 创建人：徐卫超 * 时间：2019/8/3 17:23 * 功能：审计列信息 * 备注： */@Getter@Setterpublic class FieldFillMapping extends Mapping {    /**     * 填充类型     */    private FillType type;    /**     * 填充的属性     */    private String attribute;    public FieldFillMapping(Mapping columnMeta, String attribute, FillType type) {        super(columnMeta);        this.type = type;        this.setInsertIgnore(false);        if (type.command() != SqlCommandType.UPDATE) {            this.setUpdateIgnore(true);        }        this.attribute = attribute.isEmpty() ? columnMeta.getField() : attribute;    }    public FillType getType() {        return type;    }    public String getAttribute() {        return attribute;    }}