package com.xwc.open.easybatis.core.model.table;import com.xwc.open.easybatis.core.anno.table.Id;import com.xwc.open.easybatis.core.commons.AnnotationUtils;import com.xwc.open.easybatis.core.enums.IdType;/** * 创建人：徐卫超 * 时间：2019/8/3 16:35 * 功能：主键描述对象 */public class IdMapping extends Mapping {    private IdType idType;    public IdMapping(Mapping columnMeta, IdType idType, Id id) {        super(columnMeta);        this.idType = idType;        if (idType == IdType.AUTO) {            this.setInsertIgnore(true);        }        this.mergeAnnotationAttributes(AnnotationUtils.getAnnotationAttributes(id));        this.setUpdateIgnore(true);    }    public IdType getIdType() {        return idType;    }    public void setIdType(IdType idType) {        this.idType = idType;    }}