package com.xwc.open.easybatis.core.support.impl;import com.xwc.open.easybatis.core.enums.BaseEnum;import org.apache.ibatis.type.BaseTypeHandler;import org.apache.ibatis.type.JdbcType;import org.apache.ibatis.type.MappedJdbcTypes;import org.apache.ibatis.type.MappedTypes;import java.sql.CallableStatement;import java.sql.PreparedStatement;import java.sql.ResultSet;import java.sql.SQLException;/** * 创建人：徐卫超 * 时间：2019/10/21 15:29 * 功能： * 备注： */@MappedJdbcTypes({JdbcType.INTEGER})@MappedTypes({BaseEnum.class})public class ValueEnumTypeHandler<E extends Enum<?> & BaseEnum> extends BaseTypeHandler<BaseEnum> {    private Class<E> type;    public ValueEnumTypeHandler(Class<E> type) {        if (type == null) {            throw new IllegalArgumentException("Type argument cannot be null");        }        this.type = type;    }    @Override    public void setNonNullParameter(PreparedStatement ps, int i, BaseEnum parameter, JdbcType jdbcType)            throws SQLException {        ps.setInt(i, parameter.value());    }    @Override    public E getNullableResult(ResultSet rs, String columnName) throws SQLException {        int code = rs.getInt(columnName);        return rs.wasNull() ? null : codeOf(code);    }    @Override    public E getNullableResult(ResultSet rs, int columnIndex) throws SQLException {        int code = rs.getInt(columnIndex);        return rs.wasNull() ? null : codeOf(code);    }    @Override    public E getNullableResult(CallableStatement cs, int columnIndex) throws SQLException {        int code = cs.getInt(columnIndex);        return cs.wasNull() ? null : codeOf(code);    }    private E codeOf(int code){        try {            E[] enumConstants = type.getEnumConstants();            for (E e : enumConstants) {                if (e.value() == code)                    return e;            }            return null;        } catch (Exception ex) {            throw new IllegalArgumentException("Cannot convert " + code + " to " + type.getSimpleName() + " by code value.", ex);        }    }}