package com.xwc.open.easybatis.interfaces.impl;import com.xwc.open.easybatis.anno.table.Ignore;import com.xwc.open.easybatis.interfaces.Page;/** * 创建人：徐卫超 * 时间：2019/10/16 14:57 * 功能： * 备注： */public class DefaultPage implements Page {    @Ignore    private Integer pageNum = 1;    @Ignore    private Integer pageSize = 10;    public void setPage(Integer pageNum,Integer pageSize){        this.pageNum = pageNum;        this.pageSize = pageSize;    }    @Override    public Integer getPageStart() {        if (pageNum == null || pageNum < 1) {            return 0;        }        return (pageNum - 1) * pageSize;    }    @Override    public Integer getPageOffset() {        if (pageSize == null || pageSize < 0) {            return 10;        }        return pageSize;    }}