package com.xwc.open.easybatis;import com.xwc.open.easybatis.enums.IdType;import org.apache.ibatis.session.Configuration;import org.springframework.boot.context.properties.ConfigurationProperties;/** * 创建人：徐卫超 * 时间：2019/8/3 18:17 * 功能： * 备注： */@ConfigurationProperties(prefix = "mybatis.configuration")public class EasybatisEnvironment {    /**     * 全局主键类型     */    private  static IdType type = IdType.AUTO;    private static  Boolean sqlLogger = false;    public static Boolean isSqlLogger() {        return sqlLogger;    }    public  void setSqlLogger(Boolean sqlLogger) {        EasybatisEnvironment.sqlLogger = sqlLogger;    }    public static IdType getType() {        return type;    }    public void setType(IdType type) {        this.type = type;    }}