package com.xwc.open.easybatis.excp;/** * 创建人：徐卫超 * 时间：2019/10/14 16:53 * 功能：Easybatis 所有异常的基类 * 备注： */public class EasybatisException extends RuntimeException {    public EasybatisException() {    }    public EasybatisException(String message) {        super(message);    }    public EasybatisException(String message, Throwable cause) {        super(message, cause);    }    public EasybatisException(Throwable cause) {        super(cause);    }    public EasybatisException(String message, Throwable cause, boolean enableSuppression, boolean writableStackTrace) {        super(message, cause, enableSuppression, writableStackTrace);    }}