package com.xwc.open.easybatis.meta;import com.xwc.open.easybatis.anno.condition.filter.OrderBy;import com.xwc.open.easybatis.enums.ConditionType;/** * 创建人：徐卫超 * 时间：2019/10/18 9:45 * 功能： * 备注： */public class OrderConditionAttribute extends ConditionAttribute {    /**     * 排序方式     */    private OrderBy.OrderEnum orderEnum;    /**     * 是否有自定义字段列     */    private boolean byValue;    /**     * 自定义by     */    private String by;    public OrderConditionAttribute(Attribute attribute, boolean byValue, ConditionType type, OrderBy.OrderEnum orderEnum,String by) {        super(attribute, 99, type);        this.byValue = byValue;        this.orderEnum = orderEnum;        this.by = by;    }    public String getBy() {        return by;    }    public OrderBy.OrderEnum getOrderEnum() {        return orderEnum;    }    public boolean isByValue() {        return byValue;    }}