package com.xwc.open.easy.batis.assistant;import com.xwc.open.easy.batis.anno.condition.Count;import com.xwc.open.easy.batis.anno.condition.Distinct;import com.xwc.open.easy.batis.interfaces.SyntaxTemplate;import com.xwc.open.easy.batis.meta.Attribute;import java.util.List;/** * 创建人：徐卫超 * 时间：2019/9/6 13:44 * 功能： * 备注： */public class BaseSqlAssistan {    public String selectCloums(List<Attribute> list, Count count, Distinct distinct) {        StringBuilder sb = new StringBuilder();        list.forEach(attr -> sb.append(", ").append(attr.getColunm()));        String cloums = sb.substring(1);        if (distinct != null) {            if (distinct.value().equals("*")) {                sb.insert(0, SyntaxTemplate.DISTINCT + "( ").append(cloums).append(") ");            }        }        if (count != null) {            if (distinct != null) {                sb.insert(0, SyntaxTemplate.COUNT + "( ").append(cloums).append(") ");            } else if (count.value().equals("*")) {                return "COUNT(*) ";            } else {                return "COUNT( " + count.value() + ") ";            }        }        return cloums;    }    public String insertCloums(List<Attribute> list) {        StringBuilder sb = new StringBuilder();        list.forEach(attr -> sb.append(", ").append(attr.getColunm()));        return sb.substring(1);    }    public String insertField(List<Attribute> list, String item) {        StringBuilder sb = new StringBuilder();        list.forEach(attr -> sb.append(", ").append(attr.getBatisField(item)));        return sb.substring(1);    }}