package com.xwc.open.easy.batis.assistant;import com.xwc.open.easy.batis.meta.Attribute;import java.util.List;/** * 创建人：徐卫超 * 时间：2019/9/6 13:44 * 功能： * 备注： */public class BaseSqlAssistan {    public String selectCloums(List<Attribute> list) {        StringBuilder sb = new StringBuilder();        list.forEach(attr -> sb.append(", ").append(attr.getColunm()));        return sb.substring(1);    }    public String insertCloums(List<Attribute> list) {       return selectCloums(list);    }    public String insertField(List<Attribute> list,String item) {        StringBuilder sb = new StringBuilder();        list.forEach(attr -> sb.append(", ").append(attr.getBatisField(item)));        return sb.substring(1);    }}