package com.xwc.open.easy.batis.meta;import com.xwc.open.easy.batis.interfaces.SyntaxTemplate;import java.util.ArrayList;import java.util.Collections;import java.util.Comparator;import java.util.List;/** * 创建人：徐卫超 * 时间：2019/8/3 17:51 * 功能： * 备注： */public class Condition {    private SyntaxTemplate syntaxTemplate;    private List<ConditionAttribute> conditionAttributeList = new ArrayList<>(5);    private List<Attribute> order;    private Attribute start;    private Attribute offset;    private List<Attribute> group;    private List<Attribute> updateAttributeList = new ArrayList<>(5);    public Condition(SyntaxTemplate syntaxTemplate) {        this.syntaxTemplate = syntaxTemplate;    }    public List<ConditionAttribute> queryList() {        Collections.sort(conditionAttributeList, Comparator.comparingInt(ConditionAttribute::index));        return conditionAttributeList;    }    public void queryClear() {        conditionAttributeList.clear();    }    public List<Attribute> updateAttributeList() {        return this.updateAttributeList;    }    public void addQuery(ConditionAttribute condition) {        this.conditionAttributeList.add(condition);    }    public void addUpdte(Attribute condition) {        this.updateAttributeList.add(condition);    }    public void addPage(Attribute start, Attribute offset) {        if (start != null) this.start = start;        if (offset != null) this.offset = offset;    }}