package com.xwc.open.easy.batis.meta;/** * 创建人：徐卫超 * 时间：2019/8/4 9:22 * 功能： * 备注： */public class LoglicAttribute extends Attribute {    public static final String AUDITOR = "Auditor";    private int valid;    private int invalid;    public LoglicAttribute(Attribute attribute, int valid, int invalid) {        super(attribute.getField(), attribute.getColunm(),                attribute.getGetter(), attribute.getSetter());        this.valid = valid;        this.invalid = invalid;    }    public int getValid() {        return valid;    }    public int getInvalid() {        return invalid;    }    public String getAuditor(){        return this.getField()+AUDITOR;    }}