package com.xwc.open.easy.batis.meta;import com.xwc.open.easy.batis.enums.ConditionType;/** * 创建人：徐卫超 * 时间：2019/9/10 10:17 * 功能： * 备注： */public class LoglicConditionAttribute extends ConditionAttribute {    private String loglic;    public LoglicConditionAttribute(Attribute attribute, int index, ConditionType type) {        super(attribute, index, type);    }    public LoglicConditionAttribute(LoglicAttribute loglicAttribute, boolean isSet) {        super(loglicAttribute.getField(),                loglicAttribute.getColunm(),                loglicAttribute.getGetter(),                loglicAttribute.getSetter(),                0, isSet ? ConditionType.SET_PARAM : ConditionType.EQUEL);        this.loglic = String.valueOf(isSet ? loglicAttribute.getInvalid() : loglicAttribute.getValid());    }    @Override    public String getBatisField(String perfix) {        return loglic;    }}