package com.xwc.open.easy.example.entity;import com.xwc.open.easy.batis.anno.auditor.*;import com.xwc.open.easy.batis.anno.table.Loglic;import java.util.Date;/** * 创建人：徐卫超 * 时间：2019/6/25 16:46 * 功能： * 备注： */public class BaseEntity {    /**     * 是否有效     */    @Loglic(valid = 0, invalid = 1)    private Integer valid;    /**     * 创建时间     */    @CreateTime    private Date createTime;    /**     * 创建ID     */    @CreateId    private String createId;    /**     * 创建名字     */    @CreateName    private String createName;    /**     * 更新时间     */    @UpdateTime    private Date updateTime;    /**     * 更想ID     */    @UpdateId    private String updateId;    /**     * 更新名字     */    @UpdateName    private String updateName;    public Integer getValid() {        return valid;    }    public void setValid(Integer valid) {        this.valid = valid;    }    public Date getCreateTime() {        return createTime;    }    public void setCreateTime(Date createTime) {        this.createTime = createTime;    }    public String getCreateId() {        return createId;    }    public void setCreateId(String createId) {        this.createId = createId;    }    public String getCreateName() {        return createName;    }    public void setCreateName(String createName) {        this.createName = createName;    }    public Date getUpdateTime() {        return updateTime;    }    public void setUpdateTime(Date updateTime) {        this.updateTime = updateTime;    }    public String getUpdateId() {        return updateId;    }    public void setUpdateId(String updateId) {        this.updateId = updateId;    }    public String getUpdateName() {        return updateName;    }    public void setUpdateName(String updateName) {        this.updateName = updateName;    }}