package com.xwc.open.easy.example.query;import com.xwc.open.easy.batis.anno.condition.filter.*;import lombok.Data;import java.util.List;/** * 创建人：徐卫超 * 时间：2019/10/15 10:29 * 功能： * 备注： */@Datapublic class SelectQuery {    private String code;    @NotEqual("code")    private String notEqualCode;    @IsNull("parent_code")    private Boolean parentCodeIsNull;    @NotNull("parent_code")    private Boolean parentCodeNotNull;    @In("code")    private List<String> inCode;    @NotIn("code")    private List<String> notInCode;    @Like("code")    private String likeCode;    @RightLike("code")    private String rightLikeCode;    @LeftLike("code")    private String leftLikeCode;    @GreaterThan("employeesNum")    private Integer gtEmployeesNum;    @GreaterThanEqual("employeesNum")    private Integer gtqEmployeesNum;    @LessThan("employeesNum")    private Integer ltEmployeesNum;    @LessThanEqual("employeesNum")    private Integer ltqEmployeesNum;}