package com.xwc.open.easybatis.meta;import java.lang.reflect.Method;/** * 创建人：徐卫超 * 时间：2019/8/3 16:35 * 功能：实体属性描述 * 备注： */public class Attribute {    private static final String BATIS_EL = "#{%s%s}";    private static final String BATIS_EL_ITEM = "%s.";    private String field;    private String colunm;    private Method getter;    private Method setter;    public Attribute(String field, String colunm, Method getter, Method setter) {        this.field = field;        this.colunm = colunm;        this.getter = getter;        this.setter = setter;    }    public void updateColunm(String colunm) {        if (!colunm.isEmpty()) {            this.colunm = colunm;        }    }    public void setField(String field) {        this.field = field;    }    public String getColunm() {        return colunm;    }    public Method getGetter() {        return getter;    }    public Method getSetter() {        return setter;    }    public String getField() {        return this.field;    }    public String getBatisField(String perfix) {        String newPerfix = "";        if (perfix != null) {            newPerfix = String.format(BATIS_EL_ITEM, perfix);        }        return String.format(BATIS_EL, newPerfix, this.getField());    }}