package com.xwc.open.easybatis.meta;import com.xwc.open.easybatis.enums.IdType;import org.apache.ibatis.reflection.ParamNameUtil;import java.lang.reflect.Method;import java.util.ArrayList;import java.util.List;/** * 创建人：徐卫超 * 时间：2019/9/7 8:24 * 功能： * 备注： */public class MethodMate {    /**     * 主键信息     */    private Attribute id;    /**     * 主键类型     */    private IdType type;    /**     * 参数列表名称     */    private List<String> paramNames;    private boolean haveParamKey;    private List<AuditorAttribute> auditorAttributeList = new ArrayList<>(6);    private boolean isObject;    /**     * 属性信息     */    private LoglicAttribute logic;    public MethodMate(EntityMate entityMate, Method method, boolean haveParamKey, boolean isObject) {        this.id = entityMate.getId();        this.type = entityMate.getType();        this.paramNames = ParamNameUtil.getParamNames(method);        this.haveParamKey = haveParamKey;        this.logic = entityMate.getLogic();        this.auditorAttributeList = entityMate.auditorAttributeList();    }    public LoglicAttribute getLogic() {        return logic;    }    public Attribute getId() {        return id;    }    public IdType getType() {        return type;    }    public List<String> getParamNames() {        return paramNames;    }    public boolean isHaveParamKey() {        return haveParamKey;    }    public List<AuditorAttribute> getAuditorAttributeList() {        return auditorAttributeList;    }    public boolean isObject() {        return isObject;    }}