package com.xwc.open.easybatis.meta;import com.xwc.open.easybatis.anno.condition.filter.OrderBy;import com.xwc.open.easybatis.enums.ConditionType;/** * 创建人：徐卫超 * 时间：2019/10/18 9:45 * 功能： * 备注： */public class OrderConditionAttribute extends ConditionAttribute {    private OrderBy.OrderEnum orderEnum;    private boolean byValue;    public OrderConditionAttribute(Attribute attribute, boolean byValue, ConditionType type, OrderBy.OrderEnum orderEnum) {        super(attribute, 99, type);        this.byValue = byValue;        this.orderEnum = orderEnum;    }    public OrderBy.OrderEnum getOrderEnum() {        return orderEnum;    }    public boolean isByValue() {        return byValue;    }}