package com.xwc.open.esbatis.enums;/** * 创建人：徐卫超 * 时间：2019/8/3 17:25 * 功能： * 备注： */public enum AuditorType {    /**     * 创建时间     */    CREATE_TIME(Group.CREATE),    /**     * 更新时间     */    UPDATE_TIME(Group.UPDATE),    /**     * 创建者名字     */    CREATE_NAME(Group.CREATE),    /**     * 更新者名字     */    UPDATE_NAME(Group.UPDATE),    /**     * 创建者ID     */    CREATE_ID(Group.CREATE),    /**     * 更新者ID     */    UPDATE_ID(Group.UPDATE);    private Group group;    AuditorType(Group group) {        this.group = group;    }    public enum Group {        /**         * 创建组         */        CREATE,        /**         * 更新组         */        UPDATE;    }    public Group getGroup() {        return group;    }}