package com.xwc.open.esbatis.meta;import java.lang.reflect.Method;/** * 创建人：徐卫超 * 时间：2019/8/3 16:35 * 功能：实体属性描述 * 备注： */public class Attribute {    private String field;    private String colunm;    private Method getter;    private Method setter;    public Attribute(String field, String colunm, Method getter, Method setter) {        this.field = field;        this.colunm = colunm;        this.getter = getter;        this.setter = setter;    }    public void updateColunm(String colunm) {        if (!colunm.isEmpty()) {            this.colunm = colunm;        }    }    public String getField() {        return field;    }    public String getColunm() {        return colunm;    }    public Method getGetter() {        return getter;    }    public Method getSetter() {        return setter;    }}