package com.xwc.open.esbatis.meta;import com.xwc.open.esbatis.interfaces.SyntaxTemplate;import java.util.ArrayList;import java.util.List;/** * 创建人：徐卫超 * 时间：2019/8/3 17:51 * 功能： * 备注： */public class ConditionMate {    private SyntaxTemplate syntaxTemplate;    private List<ConditionAttribute> conditionAttributeList = new ArrayList<>(5);    private ConditionAttribute order;    private ConditionAttribute start;    private ConditionAttribute offset;    private List<ConditionAttribute> setAttributeList = new ArrayList<>(5);    public ConditionMate(SyntaxTemplate syntaxTemplate) {        this.syntaxTemplate = syntaxTemplate;    }    public void add(ConditionAttribute condition) {        switch (syntaxTemplate.group(condition.getType())) {            case CLOUM_FIELD:            case CLOUM_IN_FILED:            case JUDGE_NULL:                this.conditionAttributeList.add(condition);                break;            case ORDER:                this.order = condition;                break;            case MYSQL_START:                this.start = condition;                break;            case MYSQL_OFFSET:                this.offset = condition;                break;            case SET:                this.setAttributeList.add(condition);                break;        }    }}