package com.xwc.open.esbatis.meta;import java.lang.reflect.Method;/** * 创建人：徐卫超 * 时间：2019/8/4 9:22 * 功能： * 备注： */public class LoglicAttribute extends Attribute {    private int valid;    private int invalid;    public LoglicAttribute(Attribute attribute, int valid, int invalid) {        super(attribute.getField(), attribute.getColunm(),                attribute.getGetter(), attribute.getSetter());        this.valid = valid;        this.invalid = invalid;    }    public int getValid() {        return valid;    }    public int getInvalid() {        return invalid;    }}