package com.xwc.open.example.dao;import com.xwc.open.easybatis.anno.SelectSql;import com.xwc.open.easybatis.anno.condition.Count;import com.xwc.open.easybatis.anno.condition.Distinct;import com.xwc.open.easybatis.anno.condition.filter.*;import com.xwc.open.easybatis.interfaces.BaseMapper;import com.xwc.open.example.entity.Org;import com.xwc.open.example.query.SelectQuery;import org.apache.ibatis.annotations.Mapper;import org.springframework.core.annotation.Order;import java.util.List;/** * 创建人：徐卫超 * 时间：2019/8/3 15:52 * 功能： * 备注： */@Mapperpublic interface OrgMapper extends BaseMapper<Org, String> {    @SelectSql    List<Org> query(SelectQuery selectQuery);    @SelectSql    List<Org> queryParam(@Start Integer pageStart, @Offset Integer pageSize);    @SelectSql    List<Org> manyQuery(@In List<String> code, @LessThan Integer employeesNum);    @Count()    @SelectSql    Long count(SelectQuery selectQuery);    @Count()    @SelectSql    Long queryParamCount(@Start Integer pageStart, @Offset Integer pageSize, String code);    @Distinct("parent_code")    @SelectSql    List<Org> distinct(SelectQuery selectQuery);    @Distinct("parent_code")    @SelectSql    List<Org> distinctParam(@LeftLike String code);    @SelectSql    List<Org> equalParam(String code);    @SelectSql    List<Org> notEqualParam(@NotEqual String code);    @SelectSql    List<Org> isNullParam(@IsNull Boolean parent_code);    @SelectSql    List<Org> inCodeParam(@In List<String> code);    @SelectSql    List<Org> notInCodeParam(@NotIn List<String> code);    @SelectSql    List<Org> likeCodeParam(@Like String code);    @SelectSql    List<Org> leftLikeCodeParam(@LeftLike String code);    @SelectSql    List<Org> rightLikeCodeParam(@RightLike String code);    @SelectSql    List<Org> gtEmployeesNum50Param(@GreaterThan Integer employeesNum);    @SelectSql    List<Org> gtqEmployeesNum50Param(@GreaterThanEqual Integer employeesNum);    @SelectSql    List<Org> ltEmployeesNum50Param(@LessThan Integer employeesNum);    @SelectSql    List<Org> ltqEmployeesNum50Param(@LessThanEqual Integer employeesNum);    @SelectSql    List<Org> orderEmployeesNum(@OrderBy(byValue = true) String employeesNum);}