package com.xwc.open.example.dao;import com.xwc.open.esbatis.interfaces.BaseMapper;import com.xwc.open.example.entity.User;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;/** * 创建人：徐卫超 * 时间：2019/8/3 15:52 * 功能： * 备注： */@Mapperpublic interface UserMapper extends BaseMapper<User,Long>{    @Select("SELECT * FROM t_user WHERE id = #{id}")    User findOne(@Param("id") String id);}