package com.xwc.open.example.entity;import com.xwc.open.easybatis.anno.auditor.*;import com.xwc.open.easybatis.anno.table.Loglic;import lombok.Data;import java.util.Date;/** * 创建人：徐卫超 * 时间：2019/6/25 16:46 * 功能： * 备注： */@Datapublic class BaseEntity {    /**     * 是否有效     */    @Loglic(valid = 0, invalid = 1)    private Integer valid;    /**     * 创建时间     */    @CreateTime    private Date createTime;    /**     * 创建ID     */    @CreateId    private String createId;    /**     * 创建名字     */    @CreateName    private String createName;    /**     * 更新时间     */    @UpdateTime    private Date updateTime;    /**     * 更想ID     */    @UpdateId    private String updateId;    /**     * 更新名字     */    @UpdateName    private String updateName;}