CREATE DATABASE easy_batis;USE easy_batis;SET FOREIGN_KEY_CHECKS=0;-- ------------------------------ Table structure for t_org-- ----------------------------DROP TABLE IF EXISTS `t_org`;CREATE TABLE `t_org` (	`code` varchar(32) NOT NULL COMMENT '机构编码',	`name` varchar(32) DEFAULT NULL COMMENT '机构名称',	`parent_code` varchar(32) DEFAULT NULL COMMENT '上级机构编码',	`parent_name` varchar(32) DEFAULT NULL COMMENT '上级机构名称',	`address` varchar(32) DEFAULT NULL COMMENT '机构地址',	PRIMARY KEY (`code`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='组织表';-- ------------------------------ Records of t_org-- ------------------------------ ------------------------------ Table structure for t_user-- ----------------------------DROP TABLE IF EXISTS `t_user`;CREATE TABLE `t_user` (	`id` varchar(32) NOT NULL COMMENT '用户ID',	`org_code` varchar(32) DEFAULT NULL COMMENT '机构编码',	`org_name` varchar(32) DEFAULT NULL COMMENT '机构名称',	`name` varchar(32) DEFAULT NULL COMMENT '用户名',	`age` int(3) DEFAULT NULL COMMENT '用户年龄',	`job` int(1) DEFAULT NULL COMMENT '职位 1-总监 2-经理 1-主管 3-销售 4-行政 5-技术员 6-财务',	`valid` int(1) DEFAULT NULL COMMENT '是否有效 0:有效  1:无效',	`create_time` date DEFAULT NULL COMMENT '创建时间',	`create_id` varchar(32) DEFAULT NULL COMMENT '创建用户ID',	`create_name` varchar(32) DEFAULT NULL COMMENT '创建用户名',	`update_time` date DEFAULT NULL COMMENT '创建时间',	`update_id` varchar(32) DEFAULT NULL COMMENT '更新用户ID',	`update_name` varchar(32) DEFAULT NULL COMMENT '更新用户名',	PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='用户表';-- ------------------------------ Records of t_user-- ----------------------------