package com.xwc.open.example;import com.xwc.open.example.dao.UserAuditorMapper;import com.xwc.open.example.entity.UserAudit;import com.xwc.open.example.entity.UserLogic;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;/** * 创建人：徐卫超 * 时间：2019/10/18 11:08 * 功能：审计功能测试 * 备注： */@RunWith(SpringRunner.class)@SpringBootTest(classes = Application.class)public class TestAudit {    @Autowired    private UserAuditorMapper mapper;    @Test    public void insert(){        UserAudit userLogic = new UserAudit();        userLogic.setName("xxxx");        Long insertCount = mapper.insert(userLogic);        Assert.assertEquals(insertCount,new Long(1));        mapper.delete(userLogic.getId());    }    @Test    public void delete(){        UserAudit userLogic = new UserAudit();        userLogic.setName("xxxx");        mapper.insert(userLogic);        Long deleteCount = mapper.delete(userLogic.getId());        Assert.assertEquals(deleteCount,new Long(1));    }    @Test    public void update(){        UserAudit userLogic = new UserAudit();        userLogic.setName("xxxx");        mapper.insert(userLogic);        userLogic.setName("yyyyy");        Long updateCount = mapper.update(userLogic);        Assert.assertEquals(updateCount,new Long(1));        mapper.delete(userLogic.getId());    }}