package com.xwc.open.example;import com.xwc.open.example.dao.UserLogicMapper;import com.xwc.open.example.entity.UserLogic;import com.xwc.open.example.query.UserQuery;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.util.ArrayList;import java.util.List;/** * 创建人：徐卫超 * 时间：2019/10/14 16:02 * 功能：测试逻辑CRUD功能 * 备注： @Loglic注解是用于逻辑删除的注解 */@RunWith(SpringRunner.class)@SpringBootTest(classes = Application.class)public class TestLogic {    @Autowired    private UserLogicMapper userLogicMapper;    @Test    public void selectKey(){        UserLogic userLogic = userLogicMapper.selectKey("37bd0225cc94400db744aac8dee8a001");        Assert.assertNotEquals(userLogic,null);    }    @Test    public void insert(){        UserLogic userLogic = new UserLogic();        userLogic.setName("xxxx");        Long insertCount = userLogicMapper.insert(userLogic);        Assert.assertEquals(insertCount,new Long(1));        userLogicMapper.delete(userLogic.getId());    }    @Test    public void delete(){        UserLogic userLogic = new UserLogic();        userLogic.setName("xxxx");        userLogicMapper.insert(userLogic);        Long deleteCount = userLogicMapper.delete(userLogic.getId());        Assert.assertEquals(deleteCount,new Long(1));    }    @Test    public void update(){        UserLogic userLogic = new UserLogic();        userLogic.setName("xxxx");        userLogicMapper.insert(userLogic);        userLogic.setName("yyyyy");        Long updateCount = userLogicMapper.update(userLogic);        Assert.assertEquals(updateCount,new Long(1));        userLogicMapper.delete(userLogic.getId());    }    @Test    public void query(){        ArrayList<String> idList = new ArrayList<>();        idList.add("37bd0225cc94400db744aac8dee8b001");        idList.add("37bd0225cc94400db744aac8dee8b002");        idList.add("37bd0225cc94400db744aac8dee8b003");        idList.add("37bd0225cc94400db744aac8dee8b004");        UserQuery userQuery = new UserQuery();        userQuery.setIdList(idList);        userQuery.setName("曹操");        List<UserLogic> list = userLogicMapper.query(userQuery);        System.out.println(list.toString());        Assert.assertNotEquals(new Integer(list.size()),new Integer(1));        List<UserLogic> listParam = userLogicMapper.queryParam(userQuery.getIdList(), userQuery.getName());        Assert.assertEquals(list,listParam);    }}