package com.xwc.open.example;import com.xwc.open.example.dao.UserMapper;import com.xwc.open.example.entity.User;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.util.ArrayList;/** * 创建人：徐卫超 * 时间：2019/8/3 15:58 * 功能：测试主键功能 * 备注： */@RunWith(SpringRunner.class)@SpringBootTest(classes = Application.class)public class TestPrimayKey {    @Autowired    private UserMapper userMapper;    /**     * 测试根据主键查询     */    @Test    public void primaryKeySelect() {        User user = userMapper.selectKey("37bd0225cc94400db744aac8dee8a001");        System.out.println(user.toString());        Assert.assertNotEquals(user, null);    }    /**     * 测试单条插入自动生成UUID     */    @Test    public void insertUUID() {        User user = new User();        user.setName("xwc");        Long count = userMapper.insert(user);        Assert.assertEquals(count,new Long(1));        userMapper.delete(user.getId());    }    @Test    public void delete() {        User user = new User();        user.setName("xwc");        userMapper.insert(user);        Long count = userMapper.delete(user.getId());        Assert.assertEquals(count, new Long(1));    }    /**     * 测试批量插入自动生成UUID     */    @Test    public void insertBatchUUID() {        User user1 = new User();        user1.setName("xwc");        ArrayList<User> list = new ArrayList<>();        list.add(user1);        User user = new User();        user.setName("xwc1");        list.add(user);        userMapper.insertBatch(list);        Long userDelete = userMapper.delete(user.getId());        Assert.assertEquals(userDelete,new Long(1));        Long user1Delete = userMapper.delete(user1.getId());        Assert.assertEquals(user1Delete,new Long(1));    }}